package com.example.gulei.myapplication.ui.gallery;import android.os.Bundle;import android.os.Handler;import android.os.Message;import java.lang.ref.WeakReference;public class GalleryHandler extends Handler {		private WeakReference<GalleryActivity> mGalleryActivity;	public final int SCAN_PIC = 110;		public GalleryHandler(GalleryActivity galleryActivity)	{		mGalleryActivity = new WeakReference<GalleryActivity>(galleryActivity);	}		@Override	public void handleMessage(Message msg)	{		switch (msg.what) {		case SCAN_PIC:			Bundle bundle = msg.getData();			GalleryVo galleryVo = (GalleryVo)bundle.get("gallery_vo");			if(galleryVo == null)			{				return;			}			GalleryActivity galleryActivity = mGalleryActivity.get();			if(galleryActivity == null)			{				return;			}			galleryActivity.handleMessage(galleryVo.getImgs(), galleryVo.getImageFloders());			break;		default:			break;		}	}}